import React from "react";

function PopupWithForm({name, title, isOpen, onClose, closePopup, children}) {

  const classNameOpen = `popup popup_theme_${name} ${isOpen && 'popup_opened'}`
  const classNameClose =`popup popup_theme_${name}`
  return(
    <section className={onClose ? classNameClose : classNameOpen}>
      <div className="popup__container">
        <button type="button" className="button popup__close" onClick={closePopup}>
        </button>
        <form className={`popup__content popup__content_theme_${name}`} noValidate>
          <h2 className="popup__title popup__title_theme_profile">{title}</h2>
          <div className="popup__fields">
            {children}
            <button type="submit" className="button button_submit">
              Сохранить
            </button>
          </div>
        </form>
      </div>
    </section>
  );
}

export default PopupWithForm;
