import React from "react";

const ImagePopup = ({card, onClose}) => {

  const data = (card) => {
    if(card) {
      return {
        name: card.name,
        link: card.link,
      }
    } else {
      return {
        name: '',
        link: '#',
      }
    }
  };

  const className = `popup popup_theme_image ${card && 'popup_opened'}`
  return(
    <section className={className} >
      <div className="popup__container popup__container_theme_img">
        <button type="button" className="button popup__close"
                onClick={onClose}
        >
        </button>
        <img src={card ? card.link : '#'} alt={data.name} className="popup__img" />
        <p className="popup__caption">{card ? card.name : ''}</p>
      </div>
    </section>
  );
}

export default ImagePopup;
